import React from "react";
import { connect } from "react-redux";
import { Dispatch } from "redux";
import { RootState } from "../../reducers/interfaces";
import { ShoppingCartTableProps } from "./interfaces";

import {
  bookAddedToCart,
  bookRemovedFromCart,
  allBooksRemovedFromCart,
} from "../../actions";
import "./shopping-cart-table.css";

const ShoppingCartTable: React.FC<ShoppingCartTableProps> = ({
  items,
  total,
  onIncrease,
  onDecrease,
  onDelete,
}) => {
  const renderRow = (item: { id: any; title: any; count: any; total: any; }, idx: number) => {
    const { id, title, count, total } = item;
    return (
      <tr key={id}>
        <td>{idx + 1}</td>
        <td>{title}</td>
        <td>{count}</td>
        <td>${total}</td>
        <td>
          <button
            onClick={() => onDelete(id)}
            className="btn btn-outline-danger btn-sm float-right"
          >
            <i className="fa fa-trash-o" />
          </button>
          <button
            onClick={() => onIncrease(id)}
            className="btn btn-outline-success btn-sm float-right"
          >
            <i className="fa fa-plus-circle" />
          </button>
          <button
            onClick={() => onDecrease(id)}
            className="btn btn-outline-warning btn-sm float-right"
          >
            <i className="fa fa-minus-circle" />
          </button>
        </td>
      </tr>
    );
  };

  return (
    <div className="shopping-cart-table">
      <h2>Your Order</h2>
      <table className="table">
        <thead>
          <tr>
            <th>#</th>
            <th>Item</th>
            <th>Count</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>{items.map(renderRow)}</tbody>
      </table>

      <div className="total">Total: ${total}</div>
    </div>
  );
};

const mapStateToProps = ({ bookList: { books, loading, error } }) => {
  return {
    items: shoppingCart.cartItems,
    total: shoppingCart.orderTotal,
  };
};

const mapDispatchToProps = (dispatch: Dispatch) => {
  return {
    onIncrease: (id: number) => dispatch(bookAddedToCart(id)),
    onDecrease: (id: number) => dispatch(bookRemovedFromCart(id)),
    onDelete: (id: number) => dispatch(allBooksRemovedFromCart(id)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ShoppingCartTable);
