const initialState = {
  books: [],
  loading: true
};

const reducer = (state = initialState, action) => {

  switch (action.type) {
    case 'BOOKS_REQUESTED':
      return {
        books: [],
        loading: true
      };
    case 'BOOKS_LOADED':
      return {
        books: action.payload,
        loading: false
      };
      case 'BOOKS_ERROR':
        return {
          books: [],
          loading
        }

    default:
      return state;
  }
};

export default reducer;
